mapper.select:
SELECT

FROM ds_user


        AND id = #{id,jdbcType=DECIMAL}


        AND logon_name = #{logonName,jdbcType=VARCHAR}


        AND eff_date = #{effDate,jdbcType=TIMESTAMP}


        AND exp_date = #{expDate,jdbcType=TIMESTAMP}


        AND last_name = #{lastName,jdbcType=VARCHAR}


        AND first_name = #{firstName,jdbcType=VARCHAR}


        AND account_status = #{accountStatus,jdbcType=DECIMAL}


        AND sex = #{sex,jdbcType=DECIMAL}


        AND phone = #{phone,jdbcType=VARCHAR}


        AND email = #{email,jdbcType=VARCHAR}


        AND lst_logon_date = #{lstLogonDate,jdbcType=TIMESTAMP}


        AND pwd_change_date = #{pwdChangeDate,jdbcType=TIMESTAMP}


        AND password = #{password,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=VARCHAR}


        AND create_date = #{createDate,jdbcType=TIMESTAMP}


        AND update_time = #{updateTime,jdbcType=TIMESTAMP}


        AND create_userid = #{createUserid,jdbcType=DECIMAL}


        AND account_type = #{accountType,jdbcType=DECIMAL}
